    --vortex-brand-danger: #be0700;
    --vortex-brand-bg: #0b0b0b;
    --vortex-brand-menu: #303030;
    --vortex-brand-clickable: #00a5e6;
    --vortex-brand-secondary: #1ba2b9;
    --vortex-text-color: #efefec;
