}

#steps-vortex-container {

    &:not(.active) { animation-duration: 0s !important; }

    // Cascading Stuff
    -webkit-tap-highlight-color: rgba(0, 0, 0, 0);
    font-family: 'Roboto', sans-serif;
    font-size: 12px;
    letter-spacing: .02em;
    line-height: 1.5;
    color: #efefec;
    background-color: #303030;
    color-scheme: light;
    outline: 0;

    // Box Stuff
    width: 75vw;
    height: 75vh;
    margin-left: auto;
    margin-right: auto;
    overflow: hidden;

    border: 1px solid gray;
    box-shadow: 0 3px 9px rgba(0, 0, 0, .5);

    display: flex;
    flex-direction: column;

    touch-action: none;
    text-rendering: optimizeLegibility;/*



        $$$$$$$\            $$\ $$\  $$$$$$\            $$\                 $$\
        $$  __$$\           $$ |$$ |$$  __$$\           $$ |                $  |
        $$ |  $$ | $$$$$$\  $$ |$$ |$$ /  \__|$$\   $$\ $$$$$$$\   $$$$$$\  \_/  $$$$$$$\
        $$$$$$$\ |$$  __$$\ $$ |$$ |$$ |      $$ |  $$ |$$  __$$\ $$  __$$\     $$  _____|
        $$  __$$\ $$$$$$$$ |$$ |$$ |$$ |      $$ |  $$ |$$ |  $$ |$$$$$$$$ |    \$$$$$$\
        $$ |  $$ |$$   ____|$$ |$$ |$$ |  $$\ $$ |  $$ |$$ |  $$ |$$   ____|     \____$$\
        $$$$$$$  |\$$$$$$$\ $$ |$$ |\$$$$$$  |\$$$$$$  |$$$$$$$  |\$$$$$$$\     $$$$$$$  |
        \_______/  \_______|\__|\__| \______/  \______/ \_______/  \_______|    \_______/




    */.bcd-vortex-input {
        background: none;
        border: none;
        width: 100%;
        height: min-content;

        &.js-dynamic-textarea-width {
            max-width: 100%;
            width: 80px; // The functional min-width in this case;
        }

        &:is(.option, .groupTitle):hover {
            text-decoration: underline;
        }

        &.groupTitle {
            font-weight: bold;
        }

        &.description {
            @extend .vortex-control-label;
            @extend label;

            min-height: calc(100% - 20px);
            padding: 0;
            margin: 0 !important;

            color: #fff;
            font: inherit;
        }
    }

    .vortex-modal-subtitle {
        font-size: 12px;
        line-height: 24px;
    }/*



