    .vortex-description>.vortex-control-label {
        white-space: pre-wrap;
    }

    #vortex-steps-footer {
        display: flex;
    }

    #vortex-steps-footer>* {
        flex: 1 1 0;
        text-align: center;
    }

    #vortex-steps-footer>*:first-child {
        text-align: left;
    }

    #vortex-steps-footer>*:last-child {
        text-align: right;
    }

    .vortex-installer-image {
        max-width: 100%;
        max-height: 100%;
        width: auto;
        height: auto;
        display: flex;
        justify-content: center;
        position: absolute;
    }

    .vortex-installer-image .vortex-zoomable-image {
        max-width: 100%;
        max-height: 100%;
        object-fit: contain;
    }

    .vortex-zoomable-image {
        max-width: 100%;
        max-height: 192px;
    }
}
